import pygame
import constants


class Map:

    def __init__(self, mapa, back):
        self.map = mapa
        self.background = back

        self.iconB = pygame.image.load("icons/bricksWall.png").convert()
        self.hp = pygame.image.load("icons/hp.png")

        for (i, row) in enumerate(self.map):
            for (j, el) in enumerate(row):
                if j == 0:
                    self.map[i][j] = constants.brick
                if j == 63:
                    self.map[i][j] = constants.brick
                if i == 0:
                    self.map[i][j] = constants.brick
                if i == 32:
                    self.map[i][j] = constants.brick

    def set_position(self, tank):
        self.map[tank.tank_coordinates.y][tank.tank_coordinates.x] = tank.name

    def circle(self, x, y, list_bullet):
        for bullet in list_bullet:
            if bullet is not None:
                bullet_rect = bullet.bullets
                if bullet_rect is not None and bullet_rect.colliderect(
                        pygame.Rect(x, y, constants.CELL_SIZE, constants.CELL_SIZE)):
                    list_bullet.remove(bullet)

    def draw_map(self, tank1, tank2, list_bullet1, list_bullet2):
        for row in range(constants.MATRIX_HEIGHT):
            for col in range(constants.MATRIX_WIDTH):
                x, y = col * constants.CELL_SIZE, row * constants.CELL_SIZE + constants.DIFFERENCE
                if self.map[row][col] == constants.brick:
                    constants.screen.blit(self.iconB, (x, y))
                    self.circle(x, y, list_bullet1)
                    self.circle(x, y, list_bullet2)
                elif self.map[row][col] == tank1.name and tank1.name != constants.free_cell:
                    constants.screen.blit(tank1.get_tank_icon, (x, y))
                elif self.map[row][col] == tank2.name and tank2.name != constants.free_cell:
                    constants.screen.blit(tank2.get_tank_icon, (x, y))
                elif self.map[row][col] == constants.heart:
                    constants.screen.blit(self.hp, (x, y))
        self.draw_text(tank1, tank2)

    def draw_text(self, tank1, tank2):
        font = pygame.font.Font("Font/Montserrat-BoldItalic.ttf", 14)
        my_text1 = font.render("Гравець 1", True, "black")
        my_text2 = font.render("Гравець 2", True, "black")

        pygame.draw.rect(constants.screen, (110, 30, 50), (0, 0, constants.WIDTH, 80))
        pygame.draw.rect(constants.screen, "red", (160, 20, 250, 40), 1)
        pygame.draw.rect(constants.screen, "red", (1100, 20, 250, 40), 1)
        pygame.draw.rect(constants.screen, "red", (160, 20, 25 * tank1.hp, 40))
        pygame.draw.rect(constants.screen, "red", (1100, 20, 25 * tank2.hp, 40))

        constants.screen.blit(my_text1, (180, 30))
        constants.screen.blit(my_text2, (1120, 30))

    def box_hp(self, tank):
        tank.hp += 3

    def set_position_for_hp(self, x, y):
        if self.map[x][y] == constants.free_cell:
            self.map[x][y] = constants.heart
            return False
        else:
            return True

    def move_tank(self, tank):
        coordinate_y = tank.tank_coordinates.y
        coordinate_x = tank.tank_coordinates.x
        keys = pygame.key.get_pressed()
        moving = pygame.mixer.Sound("music/moving.wav")
        moving.set_volume(0.4)
        if keys[tank.control_keys.UP]:
            moving.play()
            tank.tank_image = tank.tank_rotate.rotate_up
            coordinate_y -= 1
        elif keys[tank.control_keys.DOWN]:
            moving.play()
            tank.tank_image = tank.tank_rotate.rotate_down
            coordinate_y += 1
        elif keys[tank.control_keys.RIGHT]:
            moving.play()
            tank.tank_image = tank.tank_rotate.rotate_right
            coordinate_x += 1
        elif keys[tank.control_keys.LEFT]:
            moving.play()
            tank.tank_image = tank.tank_rotate.rotate_left
            coordinate_x -= 1

        self.check_and_move(coordinate_y, coordinate_x, tank)

    def check_and_move(self, new_coordinate_y, new_coordinate_x, tank):
        gethp = pygame.mixer.Sound("music/gethp.wav")
        if 0 <= new_coordinate_y < constants.MATRIX_HEIGHT and 0 <= new_coordinate_x < constants.MATRIX_WIDTH:
            if self.map[new_coordinate_y][new_coordinate_x] == constants.free_cell:
                self.map[tank.tank_coordinates.y][tank.tank_coordinates.x] = constants.free_cell
                self.map[new_coordinate_y][new_coordinate_x] = tank.name
                tank.tank_coordinates.y = new_coordinate_y
                tank.tank_coordinates.x = new_coordinate_x
            elif self.map[new_coordinate_y][new_coordinate_x] == constants.heart:
                gethp.play()
                self.map[tank.tank_coordinates.y][tank.tank_coordinates.x] = constants.free_cell
                self.map[new_coordinate_y][new_coordinate_x] = tank.name
                tank.tank_coordinates.y = new_coordinate_y
                tank.tank_coordinates.x = new_coordinate_x
                self.box_hp(tank)


